import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Grid from "../Grid/grid";

const columns = [
    { key: "name", name: "Name" },
    { key: "address", name: "Address" },
    { key: "price", name: "Price" }
];


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {restaurantName: ''};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange() {
        this.props.fetchRestaurantsByName(this.state.restaurantName);
    }

    render() {
      return (<div>
          <div>
              <label>
                  <span>Enter a City Name</span>
                  <input type="text" value={this.state.restaurantName} onChange={this.handleChange} />
              </label>
          </div>
          <div>
              <Grid
                columns={columns}
                rows={this.props.restaurants}
              />
          </div>
      </div>)
    }
}

export default App;

App.defaultProps = {
    restaurants: []
};
App.propTypes = {
    restaurants: PropTypes.array,
    fetchRestaurantsByName: PropTypes.func
};
